const hashService = require('./service/hashService');

module.exports = {
    hash: (password) => {
        var hashPassword = hashService(password);
        // console.log(hashPassword);
        return hashPassword;
    },
    compare: (password, hashedPassword) => {
        password = hashService(password);
        if (password === hashedPassword) {
            // console.log('matched');
            return true;
        }
        // console.log('not matched');
        return false;
    }
}