module.exports = (password) => {
    password = password.toString();
    password = password + 'hash' + password.split("").reverse().join("");;
    let hashArray = [];
    let hashPassword = "";
    let i = 0;
    while (i < password.length) {
        hashArray[i] = password[i];
        hashArray[i] = password.charCodeAt(i);
        if (hashArray[i] > 64 && hashArray[i] < 91) {
            hashArray[i] = hashArray[i] - 32;
        } else if (hashArray[i] > 96 && hashArray[i] < 123) {
            hashArray[i] = hashArray[i] + 64;
        } else if (hashArray[i] > 47 && hashArray[i] < 58) {
            hashArray[i] = hashArray[i] + 9;
        }
        if (hashArray[i] > 122) {
            hashArray[i] = hashArray[i] + 39;
        }
        hashArray[i] = String.fromCharCode(hashArray[i]) + (hashArray[i] % 10);
        hashPassword = hashPassword.toString() + hashArray[i].toString();
        i++;
    }
    return 'h' + hashPassword;
}